css:
    .ql-editor.read-mode, .print-content {
        font-family: 'Zain', sans-serif;
    }

    .main-title{
        font-family: 'Comfortaa', sans-serif;
        font-size:50px; 
        font-weight: 100; 
        margin: 10px 0;
    }

    .gray{
        color:#565F6B;
    }

    .quotation-details {
        display: -webkit-box; /* wkhtmltopdf uses this one */
        display: flex; 

        -webkit-box-pack: justify; /* wkhtmltopdf uses this one */
        justify-content: space-between;
    }

    .left-green-border{
        color:#848E98;
        width:fit-content; 
        background-color:#F0F8FF; 
        border-left:2px solid #19A264; 
        padding:0px 6px;
        font-size: 16px !important;
    }


.items {
    border: solid 1px #cfcfcf;
    margin-bottom: 24px;
}

    .items th {
        background-color:#2d95d2!important; 
        color:white !important;
        font-size: 18px !important;
    }

.print-format td, .print-format th {
    padding: 4px !important;
}


    .total-container {
        display: -webkit-box; /* wkhtmltopdf uses this one */
        display: flex;

        -webkit-box-orient: vertical; /* wkhtmltopdf uses this one */
        flex-direction: column;

        -webkit-box-align: end; /* wkhtmltopdf uses this one */
        align-items: flex-end;
        page-break-inside: avoid;
    }

    .item, .total {
        display: -webkit-box; /* wkhtmltopdf uses this one */
        display: flex; 

        -webkit-box-pack: justify; /* wkhtmltopdf uses this one */
        justify-content: space-between;

        width: 40%;
        font-weight: 500;
        margin-bottom: 16px;
    }

    .invoice-total-label {
        color: #848E98;
    }
    .total-title, .total-value {
        font-weight: 700;
    }
    .total-value {
        color: #2d95d2;
    }

    .print-format .items tr:nth-child(even){
        background-color: #fff !important;
    }

    .print-format .items tr:nth-child(odd) {
        background-color: #F0F8FF!important;
    }

    .horizontal-line{
        width:50%; 
        height:0.5px; 
        background-color:rgba(0, 0, 0, 0.2);
        margin:5px;
    }

  .footer {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    text-align: center;
    font-family: 'Zain', sans-serif;
  }

@media print {
    body{
        margin:0px !important;
        padding:0px !important;
    }

    .print-format table.items tr:first-child td {
      color: white;
    }

    .horizontal-line{
        height:1px; 
    }

    .print-format .terms .ql-editor p{ 
        color:#565F6B;
        margin-bottom:24px;
    }
      .footer {
        position: fixed;
        bottom: 0;
        left: 0;
        right: 0;
        text-align: center;
        font-family: 'Zain', sans-serif;
      }
    @page {
        size: A4;
        margin: 0.3in !important;
    }
}
