script:
frappe.ui.form.on('Task', {
	refresh(frm) {
		// your code here
		if(frm.doc.priority=="Low") {
		    frm.doc.task_weight=10;
		}
		else if (frm.doc.priority=="Medium") {
		    frm.doc.task_weight=20;
		}
		else if (frm.doc.priority=="High") {
		    frm.doc.task_weight=30;
		}
		else {
		    frm.doc.task_weight=40;
		}
	}
})
